import React, { ReactElement } from 'react';
import { StyleSheet, Text, TouchableOpacity} from 'react-native';
import { View } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import AppText from '@components/atoms/AppText';
import DropDownMenu from '@components/molecules/DropDownMenu'
import fonts from '@styles/fonts';
import colors from '@styles/colors';
import layout from '@styles/layout';

type Symptome = {
    nom: string
  }
  
  type Pathologie = {
    nom: string;
    date: string;
    more: string;
    logo: string;
  }

type Props = {
    objet: Pathologie;
    objets?: Symptome[]|Pathologie[];
    ischeckeable ?: boolean;
  };


    const BoxPathologie = ({ objet }: Props): ReactElement => {
        const onPress = (): void => {
            // Faire quelque chose lorsqu'on appuie sur la bo√Æte
          };
        
          return (
            <TouchableOpacity onPress={onPress}>
              <View style = {styles.container}>
                <AppText text={objet.nom} style={styles.title} />
                <AppText text={objet.more} style={styles.subtitle} />
                <AppText text= {"Depuis le " + objet.date} style={styles.text} />
              </View>
            </TouchableOpacity>
          );
        };
        
        const styles = {
          title: {
            fontSize: 18,
            fontWeight: 'bold' as 'bold',
            marginBottom: 5,
          },
          subtitle: {
            fontSize: 16,
            marginBottom: 5,
          },
          text: {
            fontSize: 14,
          },
          container: {
            borderWidth: 1,
            borderColor: 'black',
            padding: 10,
            marginBottom: 10,
          },
};
  
  export default BoxPathologie;

  const stylestfhfg = StyleSheet.create({
    subtitle: {
      fontSize: fonts.subtitle.fontSize,
      color: colors.black,
      marginBottom: layout.padding,
      lineHeight: fonts.subtitle.fontSize + 3,
      marginHorizontal: layout.padding
    }, 
    button: {
      borderRadius: layout.buttons.borderRadius,
      marginHorizontal: layout.padding,
    },
    textClick: {
      marginLeft: 3,
      textAlign: 'center',
    },
    text: {
      marginLeft: 25,
      lineHeight: fonts.subtitle.fontSize + 3,
      textAlign: 'center',
    },
    
  });
